<template>
  <div class="models">
    <a-input-search v-model:value="filterText" placeholder="搜索模型" />
    <div class="models-content beauty-scroll">
      <div class="models-content-title">模型</div>
      <div class="models-list">
        <template v-for="(item, index) in filtedList" :key="index">
          <div @click.stop="modelClick(item.path)" class="box-border mb-2 models-list-item">
            <div
              :class="{ border: item.path === selectedModelPath }"
              class="overflow-hidden rounded-md w-100px h-100px"
              :title="item.name"
            >
              <a-image
                v-if="item.img"
                class="cursor-pointer avatar"
                :preview="false"
                :src="item.img"
                :alt="item.name"
                fallback="data:image/png;base64,"
              />
              <div
                v-else
                class="cursor-pointer flex models-list-err"
                style="color: rgba(95,95,96);"
              >
                <picture-filled style="font-size: 50px;" />
                {{ item.name }}
              </div>
            </div>
          </div>
        </template>
      </div>
    </div>
  </div>
</template>

<script lang="ts" setup>
import { ref, onMounted, computed, reactive, onUnmounted } from 'vue';
import { PictureFilled } from '@ant-design/icons-vue';
import { GameManager } from "@/oasis";
import { cloneDeep } from 'lodash-es'
import { ModelAssetsManager } from '@/manager/ModelAssetsManager';
import { IO } from '@/oasis/utils/IO';
// 声明对外抛出的事件
const emit = defineEmits(['clickModel'])

const props = defineProps<{
  selectedModelPath: string
}>()

const ModelList = cloneDeep(ModelAssetsManager.ModelList)

/**点击切换模型 */
const modelClick = async (path: string) => {
  GameManager.ins.engine.dispatch(IO.SAVE_ALL_CONFIG)
  emit('clickModel', path)
};

const filterText = ref('');
const filtedList = computed(() => ModelList.filter(i => i.path.includes(filterText.value)))


defineExpose({
  modelClick,
})
</script>

